import React from "react";
import { Button, Spinner } from "react-bootstrap";

const CustomButton = ({
  label,
  disabled,
  isLoading,
  onClick,
  className,
  type,
  variant,
  iconClass,
  prependIcon,
}) => {
  return (
    <Button
      variant={variant || "primary"}
      onClick={onClick}
      disabled={disabled}
      className={className}
      type={type}
    >
      {isLoading ? (
        <>
          <Spinner
            as="span"
            animation="border"
            size="sm"
            role="status"
            aria-hidden="true"
          />
          <span className="sr-only">Loading...</span>
        </>
      ) : (
        <>
          {prependIcon && <i className={iconClass} />}
          {label}
          {!prependIcon && <i className={iconClass} />}
        </>
      )}
    </Button>
  );
};

export default CustomButton;
