import React, { useState } from "react";
import { Input, Button } from "../../components";
import { addTodo, deleteTodo } from "../../actions/todoAction";
import { useDispatch, useSelector } from "react-redux";
import { constants } from '../../constants/constants';

const Todo = () => {
  const [inputData, setInputData] = useState("");

  const list = useSelector((state) => state?.todoList?.list);

  const dispatch = useDispatch();

  const { addItems, buttons } = constants?.todoPage || {}
  const { addLabel, deleteLabel, editLabel} = buttons || {}

  const InlineEdit = ({ value, setValue }) => {
    const onChange = (event) => setValue(event.target.value);

    return (
      <input
        type="text"
        aria-label="Field name"
        value={value}
        onChange={onChange}
      />
    );
  };

  return (
    <div>
      <Input
        type="text"
        placeholder={addItems}
        value={inputData}
        onChange={(e) => setInputData(e.target.value)}
      />
      <Button
        label={addLabel}
        onClick={() => dispatch(addTodo(inputData), setInputData(""))}
      />
      {list &&
        list?.length > 0 &&
        list.map((value, index) => {
          return (
            <>
              <div key={index}>{value?.data}</div>
              <Button
                label={editLabel}
                variant="success"
                onClick={() => InlineEdit(value?.data)}
              />
              <Button
                label={deleteLabel}
                variant="danger"
                onClick={() => dispatch(deleteTodo(value.id))}
              />
            </>
          );
        })}
    </div>
  );
};

export default Todo;
